import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import InfiniteScroll from 'react-infinite-scroller';
import qwest from 'qwest';

const imageList = [];
// const api = {
//     baseUrl: 'https://api.soundcloud.com',
//     client_id: 'caf73ef1e709f839664ab82bef40fa96'
// };

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      books: [],
      hasMoreItems: true,
      // nextHref: null
    };
  }

  loadItems(page) {
    var self = this;

    // var url = api.baseUrl + '/users/8665091/favorites';
    // var url = "https://thebooksofmedium-api.herokuapp.com/books/sub-page?limit=20&&page=" + page;
    var url = "https://thebooksofmedium-api.herokuapp.com/books/sub-page?limit=20&&page=100";

    // if(this.state.nextHref) {
    //     url = this.state.nextHref;
    // }

    // qwest.get(url, {
    //         cache: true
    //     })

    qwest.get(url)
      .then(function (xhr, resp) {
        if (resp) {
          var books = self.state.books;
          resp.docs.map((book) => {
            // if(book.bookName == null) {
            //     book.bookName = book.bookName;
            // }

            books.push(book);
          });

          // if(resp.next_href) {
          if (true) {

            self.setState({
              books: books,
              // nextHref: resp.next_href
            });
          } else {
            self.setState({
              hasMoreItems: false
            });
          }
        }
      })
      .catch(function (e, xhr, response) {
        self.setState({
          hasMoreItems: false
        });
        console.error("no more")
      });
  }

  render() {
    const loader = <div className="loader">Loading ...</div>;

    var items = [];
    this.state.books.map((book, i) => {
      items.push(
        <div className="book" key={i}>
          <a href={book.bookName} target="_blank">
            <img src={book.bookCoverImg} width="150" height="200" />
            <p className="title">{book.bookName}</p>
          </a>
        </div>
      );
    });

    return (
      <InfiniteScroll
        pageStart={0}
        loadMore={this.loadItems.bind(this)}
        hasMore={this.state.hasMoreItems}
        hasMore={true}

        loader={loader}>

        <div className="books">
          {items}
        </div>
      </InfiniteScroll>
    );
  }
};


export default App;


