import React from 'react'
import './FiltersArea.css';

import {
    Dropdown,
    Header,
    Icon,
    Container
} from 'semantic-ui-react'


const getWidth = () => {
    const isSSR = typeof window === 'undefined'

    return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}


class FiltersArea extends React.Component {




    render() {
        let fixed = this.props.fixed;
        const categoriesOptions = [{ key: 'AL', value: 'All', text: 'All Categories' }, { key: 'W', value: 'W', text: 'Web' }, { key: 'D', value: 'Design', text: 'Design' }]
        const sortOptions = [{ key: 'R', value: 'Rank', text: 'Rank' }, { key: 'MN', value: 'MN', text: 'Mentions' }, { key: 'CL', value: 'CL', text: 'Claps' }]


        return (

            <div>
                <DesktopFiltersArea/>
                <MobileFiltersArea/>
            </div>
            // <Container >
            //     <div className={"filtersAreaContainer " + (fixed ? "stickyFiltersFixed" : "StickyFiltersNoFixed")}>
            //         <Header as='h4' className="filterItem">
            //             <Icon name='filter' />
            //             <Header.Content>
            //                 <Dropdown placeholder='All Categories' search defaultValue='All' selection options={categoriesOptions} />
            //             </Header.Content>
            //         </Header>

            //         <Header as='h4' className="filterItem">
            //             <Icon name='sort amount down' />
            //             <Header.Content>
            //                 <Dropdown placeholder='Rank' defaultValue='Rank' selection options={sortOptions} />
            //             </Header.Content>
            //         </Header>
            //     </div>
            // </Container>
        )

    }
}


class DesktopFiltersArea extends Component {
    state = {}

    // state = {}

    handleContextRef = contextRef => this.setState({ contextRef })

    hideFixedMenu = () => this.setState({ fixed: false })
    showFixedMenu = () => this.setState({ fixed: true })

    render() {
      const { children } = this.props
      const { fixed } = this.state

      const { contextRef } = this.state

      return (
        <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
          <Visibility
            once={false}
            onBottomPassed={this.showFixedMenu}
            onBottomPassedReverse={this.hideFixedMenu}
          >
            <Segment
              claasName="segment"
              // inverted
              textAlign='center'
              style={{ minHeight: 100, padding: '0.5em 0em' }}
              vertical
            >
              <div ref={this.handleContextRef}>

                <Menu
                  fixed={fixed ? 'top' : null}
                  inverted={!fixed}
                  pointing={!fixed}
                  secondary={!fixed}
                  size='large'
                  style={{ height: '58px'}}

                >
                  <Container>
                    <Menu.Item as='a' active>
                      <h1> The Books Of Medium </h1>
                    </Menu.Item>

                    <Menu.Menu position='right'>
                      <Menu.Item name='reviews' onClick={this.handleItemClick}>
                        About
                      </Menu.Item>

                    </Menu.Menu>
                  </Container>
                </Menu>
              </div>

              <HomepageHeading />
            </Segment>

          </Visibility>

          {children}
        </Responsive>
      )
    }
  }


  class MobileFiltersArea extends Component {

    render() {

      return (
        <Responsive
          as={Sidebar.Pushable}
          getWidth={getWidth}
          maxWidth={Responsive.onlyMobile.maxWidth}
        >

        Im mobile


        </Responsive>
      )
    }
  }

  MobileContainer.propTypes = {
    children: PropTypes.node,
  }

export default FiltersArea;
