import React, { Component } from 'react';
import BookList from './components/bookList/BookList';
import PropTypes from 'prop-types'
import './App.css';
import {
  Container,
  Menu,
  Segment,
  Visibility,
  Responsive,
  Sticky,
  Header,
  Icon,
  Button
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'

  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}



const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      content='The most mentioned books on Medium'
      inverted
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '0.5em' : '1.5em',
      }}
    />
    <Header
      as='h2'
      content='We analysed more than 40 000 000 questions and answers on stackoverflow.com to bring you the top of most mentioned books (5720 in total)'
      inverted
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
      }}
    />
    <Button primary size='huge'>
      Get Started
      <Icon name='right arrow' />
    </Button>
  </Container>
)




const ResponsiveContainer = ({ children }) => (
  <div>
    <DesktopContainer>{children}</DesktopContainer>
    {/* <MobileContainer>{children}</MobileContainer> */}
  </div>
)


class DesktopContainer extends Component {
  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {
    const { children } = this.props
    const { fixed } = this.state

    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 300, padding: '1em 0em' }}
            vertical
          >
            <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >
              <Container>
                <Menu.Item as='a' active>
                  <h1> The Books Of Medium </h1>
                </Menu.Item>

                <Menu.Menu position='right'>
                  <Menu.Item name='reviews' onClick={this.handleItemClick}>
                    About
                </Menu.Item>

                </Menu.Menu>
              </Container>
            </Menu>

            <HomepageHeading />
          </Segment>

        </Visibility>

        {children}
      </Responsive>
    )
  }
}



DesktopContainer.propTypes = {
  children: PropTypes.node,
}


class App extends Component {

  render() {
    return (
      <ResponsiveContainer>

        <Container text style={{ marginTop: '7em' }} textAlign='center'>
          <BookList />
        </Container>
      </ResponsiveContainer>
    );
  }
};

export default App;
