import React, { Component } from 'react';
import BookList from './components/bookList/BookList';
import Toolbar from './components/toolbar/Toolbar';
import './App.css';
// import { Dropdown, Container, Menu, Segment } from 'semantic-ui-react'
import {
  Container,
  Button,
  Dropdown,
  Grid,
  Header,
  Image,
  List,
  Menu,
  Segment,
  Visibility,
  Responsive
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'

  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}


class App extends Component {


  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {

    const { children } = this.props
    const { fixed } = this.state


    return (

      // <div>
      //   <Menu fixed="top">
      //     <Menu.Item
      //       name='logo'
      //       onClick={this.handleItemClick}
      //     >
      //       {/* <img src='../../images/bookFav.ico' /> */}
      //       <h1> The Books Of Medium </h1>
      //     </Menu.Item>

      //     <Menu.Menu position='right'>
      //       <Menu.Item name='reviews' onClick={this.handleItemClick}>
      //         About
      //       </Menu.Item>

      //     </Menu.Menu>

      //   </Menu>
      //   <div className="intro">
      //     <Container text style={{ paddingTop: '7em' }} >
      //       <Header as='h1'>Semantic UI React Fixed Template</Header>
      //       <p>The most mentions books on medium.</p>
      //       <p>
      //       We analysed more than 40 000 000 questions and answers on stackoverflow.com to bring you the top of most mentioned books (5720 in total)
      //       </p>
      //     </Container>
      //   </div>
      //   <Container text style={{ marginTop: '7em' }}  textAlign='center'>
      //     <BookList />
      //   </Container>


      // </div>

      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
        <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 700, padding: '1em 0em' }}
            vertical
          >


          <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >
              <Container>
                <Menu.Item as='a' active>
                  Home
                </Menu.Item>
                <Menu.Item as='a'>Work</Menu.Item>
                <Menu.Item as='a'>Company</Menu.Item>
                <Menu.Item as='a'>Careers</Menu.Item>
                <Menu.Item position='right'>
                  <Button as='a' inverted={!fixed}>
                    Log in
                  </Button>
                  <Button as='a' inverted={!fixed} primary={fixed} style={{ marginLeft: '0.5em' }}>
                    Sign Up
                  </Button>
                </Menu.Item>
              </Container>
            </Menu>


          </Segment>

        </Visibility>


      </Responsive>



    );
  }
};



export default App;
