import React from 'react'
import InfiniteScroll from 'react-infinite-scroller';
import BookItem from '../bookItem/BookItem';

import qwest from 'qwest';
import './BookList.css';
import {
    Item,
    Container,
    Dropdown,
    Visibility,
    Header,
    Icon
} from 'semantic-ui-react'



class BookList extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            books: [],
            hasMoreItems: true,
        };
    }

    hideFixedMenu = () => this.setState({ fixed: false })
    showFixedMenu = () => this.setState({ fixed: true })

    loadItems(page) {
        var self = this;
        var url = "https://thebooksofmedium-api.herokuapp.com/books/sub-page?limit=50&&page=" + page;

        qwest.get(url, {
            cache: true
        })
            .then(function (xhr, resp) {
                if (resp) {
                    var books = self.state.books;
                    resp.docs.map((book) => {
                        books.push(book);
                    });
                    self.setState({
                        books: books,
                    });
                }
            })
            .catch(function (e, xhr, response) {
                self.setState({
                    hasMoreItems: false
                });
                console.error("no more")
            });
    }




    render() {
        const loader = <div className="loader">Loading ...</div>;



        const { fixed } = this.state

        const categoriesOptions = [{ key: 'AL', value: 'AL', text: 'Alabama' }, { key: 'IL', value: 'IL', text: 'Israel' }]

        const sortOptions = [{ key: 'R', value: 'R', text: 'Rank' }, { key: 'MN', value: 'MN', text: 'Mentions' }, { key: 'CL', value: 'CL', text: 'Claps' }]




        var items = [];
        this.state.books.map((bookObject, i) => {
            items.push(

                <BookItem
                    key={i}
                    id={bookObject.id}
                    bookName={bookObject.bookName}
                    bookAuthor={bookObject.bookAuthor}
                    amazonLink={bookObject.amazonLink}
                    bookCoverImg={bookObject.bookCoverImg}
                    totalMentionsClaps={bookObject.totalMentionsClaps}
                    numberOfMentions={bookObject.numberOfMentions}
                    score={bookObject.score}
                    index={i}
                />
            );
        });


        return (
            <div>

                {/* <Sticky>
                    Test
                </Sticky> */}

                <Visibility
                    once={false}
                    onTopPassed={this.showFixedMenu}
                    onTopPassedReverse={this.hideFixedMenu}
                    className="stickyFiltersWarpper">

                    <Container text style={{ marginTop: '0em' }} textAlign='center' >
                        <div className={"filtersAreaContainer " + (fixed ? "stickyFilters" : "StickyFiltersNoFixed")}>


                            <Header as='h4'>
                                <Icon name='filter' />
                                <Header.Content>
                                    <Dropdown placeholder='All Categories' search 
                                     selection options={categoriesOptions} />
                                </Header.Content>
                            </Header>

                            <Header as='h4'>
                                <Icon name='sort amount down' />
                                <Header.Content>
                                    <Dropdown placeholder='Rank' search selection options={sortOptions} />
                                </Header.Content>
                            </Header>
                        </div>
                    </Container>

                </Visibility>

                <Container text textAlign='center' className="bookListContainer">


                    <InfiniteScroll
                        pageStart={0}
                        loadMore={this.loadItems.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={loader}>

                        <div className="books">
                            <Item.Group divided>
                                {items}
                            </Item.Group>
                        </div>
                    </InfiniteScroll>

                </Container>


            </div>


        );
    }
}

export default BookList;
