import React from 'react'
// import InfiniteScroll from 'react-infinite-scroller';
import InfiniteScroll from 'react-infinite-scroll-component';
import BookItem from '../bookItem/BookItem';
import FiltersArea from '../filtersArea/FiltersArea';
import qwest from 'qwest';
import './BookList.css';
import {
    Item,
    Container,
    Visibility,
    Responsive
} from 'semantic-ui-react'


const getWidth = () => {
    const isSSR = typeof window === 'undefined'

    return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}



class BookList extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            books: [],
            hasMoreItems: true,
            sorter: "score",
            page: 0
        };

    }

    // componentDidMount() {
    //     this.loadItems(0);
    //   }


    hideFixedMenu = () => this.setState({ fixed: false })
    showFixedMenu = () => this.setState({ fixed: true })



    onSorterChange = (event, data) => {
        this.setState({ sorter: data.value })
        // this.setState({ books: []})


        // this.forceUpdate()
        console.log("dataaaaaa", data.value)
    }

    componentDidMount() {
        // do some paginated fetch
        this.loadMore()
      }


      loadMore = () => {
        this.setState({ isLoading: true, error: undefined })
        var url = "https://thebooksofmedium-api.herokuapp.com/books/" + this.state.sorter + "/sub-page?limit=50&&page=" + this.state.page;
        fetch(url)
          .then(res => res.json())
          .then(
            res => {
              this.setState(state => ({ 
                books: [...state.books, ...res.docs], 
                isLoading: false ,
                page: this.state.page++
              }))
            },
            error => {
              this.setState({ isLoading: false, error })
            }
        )
      }

    // loadItems(page) {
    //     console.log("page", page)
    //     var self = this;
    //     var url = "https://thebooksofmedium-api.herokuapp.com/books/" + this.state.sorter + "/sub-page?limit=50&&page=" + page;




    //     qwest.get(url, {
    //         cache: false
    //     })
    //         .then(function (xhr, resp) {
    //             if (resp) {
    //                 var books = self.state.books;
    //                 resp.docs.map((book) => {
    //                     books.push(book);
    //                 });
    //                 self.setState({
    //                     books: books,
    //                 });
    //             }
    //         })
    //         .catch(function (e, xhr, response) {
    //             self.setState({
    //                 hasMoreItems: false
    //             });
    //             console.error("no more")
    //         });
    // }




    render() {
        const loader = <div className="loader">Loading ...</div>;

        const { hasMoreItems, fixed, sorter, pageStart } = this.state


        var items = [];
        this.state.books.map((bookObject, i) => {

            let totalMentionsClaps = nFormatter(bookObject.totalMentionsClaps, 1);
            items.push(

                <BookItem
                    key={i}
                    id={bookObject.id}
                    bookName={bookObject.bookName}
                    bookAuthor={bookObject.bookAuthor}
                    amazonLink={bookObject.amazonLink}
                    bookCoverImg={bookObject.bookCoverImg}
                    totalMentionsClaps={totalMentionsClaps}
                    // totalMentionsClaps={bookObject.totalMentionsClaps}

                    numberOfMentions={bookObject.numberOfMentions}
                    score={bookObject.score}
                    index={i}
                />
            );
        });


        return (

            <div>



                {/* // Desktop layout */}

                <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>

                    <div className="bookListWrapperDesktop">

                        <Visibility
                            once={false}
                            onTopPassed={this.showFixedMenu}
                            onTopPassedReverse={this.hideFixedMenu}
                            className="stickyFiltersWarpper">

                            <FiltersArea fixed={fixed} sorter={sorter} onSorterChange={this.onSorterChange} />

                        </Visibility>

                        <Container text className="bookListContainer">


                            {/* <InfiniteScroll
                            pageStart={0}
                            loadMore={this.loadItems.bind(this)}
                            hasMore={hasMoreItems}
                            loader={loader}>

                            <div className="books">
                                <Item.Group divided>
                                    {items}
                                </Item.Group>
                            </div>
                        </InfiniteScroll> */}


                            <InfiniteScroll
                                dataLength={items.length} //This is important field to render the next data
                                next={this.loadMore}
                                hasMore={true}
                                loader={<h4>Loading...</h4>}
                                endMessage={
                                    <p style={{ textAlign: 'center' }}>
                                        <b>Yay! You have seen it all</b>
                                    </p>
                                }
                                >
                                {items}
                            </InfiniteScroll>

                        </Container>


                    </div>

                </Responsive>


                {/* // Mobile layout */}


                <Responsive getWidth={getWidth} maxWidth={Responsive.onlyMobile.maxWidth} >

                    <div className="bookListWrapperMobile">

                        <Visibility
                            once={false}
                            onTopPassed={this.showFixedMenu}
                            onTopPassedReverse={this.hideFixedMenu}
                            className="stickyFiltersWarpper">

                            <FiltersArea fixed={fixed} sorter={this.state.sorter} />

                        </Visibility>

                        <Container className="bookListContainerMobile">


                            <InfiniteScroll
                                pageStart={0}
                                loadMore={this.loadItems.bind(this)}
                                hasMore={this.state.hasMoreItems}
                                loader={loader}>

                                <div className="books">
                                    <Item.Group divided>
                                        {items}
                                    </Item.Group>
                                </div>
                            </InfiniteScroll>

                        </Container>


                    </div>


                </Responsive>


            </div>










        );
    }
}



const nFormatter = (num, digits) => {
    var si = [
        { value: 1, symbol: "" },
        { value: 1E3, symbol: "k" },
        { value: 1E6, symbol: "M" },
        { value: 1E9, symbol: "G" },
        { value: 1E12, symbol: "T" },
        { value: 1E15, symbol: "P" },
        { value: 1E18, symbol: "E" }
    ];
    var rx = /\.0+$|(\.[0-9]*[1-9])0+$/;
    var i;
    for (i = si.length - 1; i > 0; i--) {
        if (num >= si[i].value) {
            break;
        }
    }
    return (num / si[i].value).toFixed(digits).replace(rx, "$1") + si[i].symbol;
}


export default BookList;
