import React, { Component } from 'react';
import PropTypes from 'prop-types'
import './ResponsiveContainer.css';
import {
  Container,
  Menu,
  Segment,
  Visibility,
  Responsive,
  Modal,
  Header,
  Image,
  Dropdown,
  Button
} from 'semantic-ui-react'



const getWidth = () => {
  const isSSR = typeof window === 'undefined'

  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}



/* eslint-disable react/no-multi-comp */
/* Heads up! HomepageHeading uses inline styling, however it's not the best practice. Use CSS or styled components for
 * such things.
 */
const HomepageHeading = ({ mobile }) => (
  <Container text textAlign='center' >
    <Header
      as='h2'
      content='The most mentioned books on Medium'
      inverted
      style={{
        fontSize: mobile ? '1.1em' : '1.5em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '0em' : '1.5em',
      }}
    />
    <Header
      as='h3'
      // content='I have analyzed every book ever mentioned on Medium. Here are the most popular ones.'
      inverted
      style={{
        fontSize: mobile ? '0.9em' : '1em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1em',
        marginBottom: mobile ? '0.5em' : '1em',
      }}>
      I have analyzed every book ever mentioned on Medium. Here are the most popular ones. 
      </Header>

  </Container>
)


HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}



const ResponsiveContainer = ({ children }) => (
  <div>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>
  </div>
)




class DesktopContainer extends Component {
  state = {}

  // state = {}

  handleContextRef = contextRef => this.setState({ contextRef })

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })


  //TODO - duplicate method
  onLogoClick = () => {
    window.location = '/';
  }

  render() {
    const { children } = this.props
    const { fixed } = this.state

    const { contextRef } = this.state

    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            claasName="segment"
            // inverted
            textAlign='center'
            style={{ minHeight: 100, padding: '0.5em 0em' }}
            vertical
          >
            <div ref={this.handleContextRef}>

              <Menu
                fixed={fixed ? 'top' : null}
                inverted={!fixed}
                pointing={!fixed}
                secondary={!fixed}
                size='large'
                style={{ height: '58px' }}

              >
                <Container>
                  <Menu.Item as='a' active onClick={this.onLogoClick}>
                    <h1> The Books Of Medium </h1>
                  </Menu.Item>




                  <Menu.Menu position='right'>
                    <ModalModalExample />


                  </Menu.Menu>
                </Container>
              </Menu>
            </div>

            <HomepageHeading />
          </Segment>

        </Visibility>

        {children}
      </Responsive>
    )
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node,
}


class MobileContainer extends Component {
  state = {}

  handleSidebarHide = () => this.setState({ sidebarOpened: false })

  handleToggle = () => this.setState({ sidebarOpened: true })

  //TODO - duplicate method
  onLogoClick = () => {
    window.location = '/';
  }


  render() {
    const { children } = this.props
    const { sidebarOpened } = this.state

    return (
      <Responsive
        getWidth={getWidth}
        maxWidth={Responsive.onlyMobile.maxWidth}
        className="mobileResponsiveContainer"
      >




        <Menu  >
          <Container>
            <Menu.Item as='a' header onClick={this.onLogoClick}>
              {/* <Image size='mini' src='/logo.png' style={{ marginRight: '0em' }} /> */}
              The Books Of Medium

          </Menu.Item>

            <Menu.Menu position='right'>


              {/* Do not delete */}
              {/* // <Dropdown item simple text='About' position='right'>
              //   <Dropdown.Menu>
              //     <Dropdown.Item>List Item</Dropdown.Item>
              //     <Dropdown.Item>List Item</Dropdown.Item>
              //     <Dropdown.Item>List Item</Dropdown.Item>
              //   </Dropdown.Menu>
              // </Dropdown> */}

              {/* <Menu.Item
                name='About'
                content='About'
                onClick={this.handleItemClick}
              /> */}
              <ModalModalExample />

            </Menu.Menu>




          </Container>
        </Menu>

        <Segment className="mobileSegment" >

          <HomepageHeading mobile />

        </Segment>

        {children}
        {/* </Sidebar.Pusher> */}
      </Responsive>
    )
  }
}

MobileContainer.propTypes = {
  children: PropTypes.node,
}



const ModalModalExample = () => (
  <Modal
    size="mini"
    closeIcon
    trigger={
      // <Button>Show Modal</Button>
      <Menu.Item as='a' header >
        {/* <Image size='mini' src='/logo.png' style={{ marginRight: '0em' }} /> */}
        About

          </Menu.Item>
    }>
    <Modal.Header>Disclaimer</Modal.Header>
    <Modal.Content >
      <Modal.Description>
        <p>TheBooksOfMedium is a participant in the Amazon Services LLC Associates Program,
           an affiliate advertising program designed to provide a means for sites to earn
            advertising fees by advertising and linking to Amazon.com.</p>
      </Modal.Description>
    </Modal.Content>
  </Modal>


)


export default ResponsiveContainer;
