import React from 'react'
import InfiniteScroll from 'react-infinite-scroller';
import BookItem from '../bookItem/BookItem';
import { Item, Sticky, Dropdown, Visibility } from 'semantic-ui-react'
import qwest from 'qwest';
import './BookList.css';



class BookList extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            books: [],
            hasMoreItems: true,
        };
    }

    loadItems(page) {
        var self = this;
        var url = "https://thebooksofmedium-api.herokuapp.com/books/sub-page?limit=50&&page=" + page;

        qwest.get(url, {
            cache: true
        })
            .then(function (xhr, resp) {
                if (resp) {
                    var books = self.state.books;
                    resp.docs.map((book) => {
                        books.push(book);
                    });
                    self.setState({
                        books: books,
                    });
                }
            })
            .catch(function (e, xhr, response) {
                self.setState({
                    hasMoreItems: false
                });
                console.error("no more")
            });
    }


    render() {
        const loader = <div className="loader">Loading ...</div>;



        const { fixed } = this.state


        var items = [];
        this.state.books.map((bookObject, i) => {
            items.push(

                <BookItem
                    key={i}
                    id={bookObject.id}
                    bookName={bookObject.bookName}
                    bookAuthor={bookObject.bookAuthor}
                    amazonLink={bookObject.amazonLink}
                    bookCoverImg={bookObject.bookCoverImg}
                    totalMentionsClaps={bookObject.totalMentionsClaps}
                    numberOfMentions={bookObject.numberOfMentions}
                    score={bookObject.score}
                    index={i}
                />
            );
        });


        return (
            <div>

                {/* <Sticky>
                    Test
                </Sticky> */}


                <Visibility
                    once={false}
                    onTopPassed={this.showFixedMenu}
                    onTopPassedReverse={this.hideFixedMenu}>
                    <div className="stickyFilters" inverted={!fixed}>
                        <Dropdown text='File'>
                            <Dropdown.Menu>
                                <Dropdown.Item text='New' />
                                <Dropdown.Item text='Open...' description='ctrl + o' />
                                <Dropdown.Item text='Save as...' description='ctrl + s' />
                            </Dropdown.Menu>
                        </Dropdown>
                    </div>

                </Visibility>

                <InfiniteScroll
                    pageStart={0}
                    loadMore={this.loadItems.bind(this)}
                    hasMore={this.state.hasMoreItems}
                    loader={loader}>

                    <div className="books">
                        <Item.Group divided>
                            {items}
                        </Item.Group>
                    </div>
                </InfiniteScroll>
            </div>


        );
    }
}

export default BookList;
